import React from "react";
import AnimationRevealPage from "helpers/AnimationRevealPage.js";
import tw from "twin.macro";
import styled from "styled-components";
import { css } from "styled-components/macro"; //eslint-disable-line
import Header from "components/headers/light.js";
import Footer from "components/footers/MiniCenteredFooter.js";
import { Container, ContentWithPaddingXl } from "components/misc/Layouts";
import { SectionHeading, Subheading as SubheadingBase } from "components/misc/Headings";
import { PrimaryButton as PrimaryButtonBase } from "components/misc/Buttons";

const HeroContainer = tw.div`relative py-20 md:py-24`;
const Column = tw.div`w-full max-w-md mx-auto md:max-w-none md:mx-0`;
const TextColumn = tw(Column)`md:w-7/12 mt-16 md:mt-0 md:mr-12 lg:mr-16 md:order-first`;

const TextContent = tw.div`lg:py-8 text-center md:text-left`;
const Subheading = tw(SubheadingBase)`text-center md:text-left text-primary-500`;
const Heading = tw(SectionHeading)`mt-4 font-black text-left text-3xl sm:text-4xl lg:text-5xl text-center md:text-left leading-tight`;
const Description = tw.p`mt-4 text-center md:text-left text-sm md:text-base lg:text-lg font-medium leading-relaxed text-gray-600`;

const PrimaryButton = styled(PrimaryButtonBase)(props => [
  tw`mt-8 md:mt-8 text-sm inline-block mx-auto md:mx-0`,
  props.buttonRounded && tw`rounded-full`
]);

const FeatureList = tw.ul`mt-12 leading-loose`;
const Feature = tw.li`flex items-center`;
const FeatureIcon = tw.span`w-5 h-5 bg-primary-500 rounded-full flex-shrink-0`;
const FeatureText = tw.span`ml-3 font-medium text-gray-700`;

const StatsContainer = tw.div`mt-20 flex flex-col sm:flex-row justify-center items-center sm:items-stretch`;
const Stat = tw.div`flex flex-col items-center text-center p-4 sm:p-8 tracking-wide`;
const StatValue = tw.div`text-4xl sm:text-5xl font-bold text-primary-500`;
const StatLabel = tw.div`text-sm sm:text-base text-gray-600 mt-2 font-medium`;

export default () => {
  return (
    <AnimationRevealPage>
      <Header />
      <Container>
        <ContentWithPaddingXl>
          <HeroContainer>
            <TextContent>
              <Subheading>Research Project</Subheading>
              <Heading>Judging the Judges</Heading>
              <Description>
                Welcome to our research project. We are dedicated to evaluating and analyzing 
                the capabilities and limitations of LLM-as-a-Judge systems, providing insights 
                and benchmarks for the development of AI judgment systems.
              </Description>
              <PrimaryButton as="a" href="/leaderboard" buttonRounded={true}>
                View Leaderboard
              </PrimaryButton>
            </TextContent>
          </HeroContainer>

          <StatsContainer>
            <Stat>
              <StatValue>10+</StatValue>
              <StatLabel>Models Evaluated</StatLabel>
            </Stat>
            <Stat>
              <StatValue>1000+</StatValue>
              <StatLabel>Test Cases</StatLabel>
            </Stat>
            <Stat>
              <StatValue>5+</StatValue>
              <StatLabel>Evaluation Dimensions</StatLabel>
            </Stat>
          </StatsContainer>

          <FeatureList>
            <Feature>
              <FeatureIcon />
              <FeatureText>Comprehensive evaluation framework for LLM judges</FeatureText>
            </Feature>
            <Feature>
              <FeatureIcon />
              <FeatureText>Diverse prompts and examples for testing</FeatureText>
            </Feature>
            <Feature>
              <FeatureIcon />
              <FeatureText>Detailed results and analysis</FeatureText>
            </Feature>
          </FeatureList>
        </ContentWithPaddingXl>
      </Container>
      <Footer />
    </AnimationRevealPage>
  );
};
